import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import api from '../services/api';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [prices, setPrices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDashboardData();

    // 5Ï¥àÎßàÎã§ Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏
    const interval = setInterval(loadDashboardData, 5000);
    return () => clearInterval(interval);
  }, []);

  const loadDashboardData = async () => {
    try {
      setError(null);
      const response = await api.getDashboard();

      if (response.success) {
        setDashboardData(response.data);

        // Í∞ÄÍ≤© Îç∞Ïù¥ÌÑ∞Î•º Ï∞®Ìä∏Ïóê ÎßûÍ≤å Î≥ÄÌôò
        if (response.data.priceHistory) {
          const chartData = response.data.priceHistory.map((item, index) => ({
            time: new Date(item.time).toLocaleTimeString(),
            price: item.close,
            volume: item.volume
          }));
          setPrices(chartData);
        }
      } else {
        setError(response.error);
      }
    } catch (err) {
      setError(err.message);
      console.error('Dashboard load failed:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">üìä ÎåÄÏãúÎ≥¥Îìú Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</div>;
  }

  if (error) {
    return (
      <div className="error">
        ‚ùå Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®: {error}
        <button onClick={loadDashboardData} style={{ marginLeft: '1rem' }}>üîÑ Ïû¨ÏãúÎèÑ</button>
      </div>
    );
  }

  if (!dashboardData) {
    return <div className="error">‚ùå Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.</div>;
  }

  return (
    <div className="dashboard">
      <div className="card">
        <h2>üìä Ìä∏Î†àÏù¥Îî© ÎåÄÏãúÎ≥¥Îìú</h2>

        <div className="stats-grid">
          <div className="stat-card">
            <h3>Ï¥ù ÏûêÏÇ∞</h3>
            <p>${dashboardData.account?.totalBalance?.toLocaleString() || '0'}</p>
          </div>
          <div className="stat-card">
            <h3>ÏÇ¨Ïö© Í∞ÄÎä•</h3>
            <p>${dashboardData.account?.availableBalance?.toLocaleString() || '0'}</p>
          </div>
          <div className="stat-card">
            <h3>ÌòÑÏû¨ Ìè¨ÏßÄÏÖò</h3>
            <p>{dashboardData.openPositions?.length || 0}</p>
          </div>
          <div className="stat-card">
            <h3>Ïò§Îäò ÏàòÏùµ</h3>
            <p className={dashboardData.todayProfit >= 0 ? 'profit' : 'loss'}>
              ${dashboardData.todayProfit?.toLocaleString() || '0'}
            </p>
          </div>
        </div>

        <div className="chart-section card">
          <h3>üìà BTCUSDT Ïã§ÏãúÍ∞Ñ Ï∞®Ìä∏</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={prices}>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
              <XAxis dataKey="time" stroke="rgba(255,255,255,0.7)" />
              <YAxis stroke="rgba(255,255,255,0.7)" />
              <Tooltip 
                contentStyle={{ 
                  backgroundColor: 'rgba(15, 15, 35, 0.9)',
                  border: '1px solid rgba(0, 212, 255, 0.5)',
                  borderRadius: '8px'
                }}
              />
              <Legend />
              <Line 
                type="monotone" 
                dataKey="price" 
                stroke="#00d4ff" 
                strokeWidth={2}
                dot={false}
                name="Í∞ÄÍ≤© (USDT)"
              />
            </LineChart>
          </ResponsiveContainer>
        </div>

        <div className="prices-grid card">
          <h3>üí± Ïã§ÏãúÍ∞Ñ Í∞ÄÍ≤©</h3>
          <div className="stats-grid">
            {dashboardData.prices?.map((price, index) => (
